CREATE TABLE `tpages` (
  `id` mediumint(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(200) DEFAULT NULL,
  `alias` varchar(200) CHARACTER SET latin1 DEFAULT NULL,
  `content` text,
